import "./Style.css"
import { BsSearch } from 'react-icons/bs';
import { useState, useEffect } from "react"
import Invoice from './Invoice';
const url = `https://www.themealdb.com/api/json/v1/1/search.php?s`

export default function Home() {
    const [apidata, setapidata] = useState([])
    const [allsearch, setAllSearch] = useState('')
    const [price, setprice] = useState(80)
    const [sum, setsum] = useState(0)
    const [Item, setItem] = useState([])
    const [bool, setBool] = useState(true)
    const fetchData = async () => {
        const response = await fetch(url)
        const data = await response.json()
        setapidata(data.meals)
    }
    fetchData()

    const Function = !allsearch ? apidata : apidata.filter((item) =>
        item.strMeal.toLowerCase().includes(allsearch.toLowerCase())
    )

    useEffect(() => {
        fetchData()
    }, [])

    return (
        <div id="maindiv" >

            {bool ? <div>
                <div id="seconddiv">
                    <div id="thirddiv">
                        <img src="	https://www.themealdb.com/images/logo-small.png" alt="" /><button id="home1">Home</button> <button id="api" >API</button> <button id="Forum" >Forum</button ><button id="img1"><img src='https://www.themealdb.com/images/facebook_icon.png' /></button><button id="img2"><img src='https://www.themealdb.com/images/twitter_icon.png' /></button> <input id="input1" type="text" placeholder='Search' /></div>
                </div>

                <div id="divfour">
                    <h1 id="welcome">Welcome to TheMealDB</h1>
                    <div id="divfive"><img src='https://www.themealdb.com/images/meal-icon.png' />
                        <p id="p1">Welcome to TheMealDB: An open, crowd-sourced database of Recipes from around the world.</p>
                        <p id="p2">We also offer a <span style={{ color: "#d57d1f" }}>free JSON API</span>  for anyone wanting to use it, with additional features for subscribers.</p>
                        <button id="button6"> <img id="img3" src='data:image/svg+xml;base64,' /> <p id="p3">Subscribe</p> </button>
                        <p id="p4">Click to Support $2 per month (cancel anytime) </p>
                        <p id="p5">Currently 50 supporters</p><img id="img4" src='https://www.themealdb.com/images/meal-icon.png' /></div>
                </div >

                <div id="divsix">
                    <hr id="hr1" />
                    <input id="input2" type="search" value={allsearch} placeholder='Search for a Meal...' onChange={(e) => {
                        setAllSearch(e.target.value)
                    }} /><button id="button7"><div id="icon">< BsSearch /></div>
                    </button><button id="button8" onClick={()=>{
                        setBool(false)
                    }}>Invoice</button>
                    <hr id="hr2" />
                    <h5 id="h5"><img src='https://www.themealdb.com/images/icons/meal-icon6.png' /> Total Meals: 285 <img id="img5" src='https://www.themealdb.com/images/icons/meal-icon4.png' /> Total Ingredients: 574 <img src='https://www.themealdb.com/images/icons/image2.png' /> Images: 285</h5>
                </div>
                <div id="home" >

                    {
                        Function.map((item, index) => {
                            return (
                                <div id="grid" key={index} >
                                    <h2 id="h2">{item.idMeal} </h2>
                                    <h3 id="h3" >($100)</h3>
                                    <img id='foodimg' src={item.strMealThumb} />
                                    <h2 id="h22" >{item.strMeal} </h2>
                                    <button id="button9" onClick={() => {
                                        setItem([...Item, { Discription: item.strMeal, Price: price }])
                                        setprice(100)
                                        setsum(sum + price)
                                    }}>ADD</button>
                                </div>
                            )
                        })

                    }
                </div>

            </div> :
                <div className="heading1">
                    <button onClick={()=>{
                        setBool(true)
                    }}>Back</button>
                    <Invoice obj={Item} />
                    <div className="total"><h4 className="span" >TOTAL:</h4><p className="span"></p><p className="span">{sum}</p></div>
                </div>
            }

        </div>

    )
}





